settings.custom_css:
.degradexquiz {
    color: transparent !important;
    background-image: linear-gradient(to right, #96cce7, #0284c7);
    background-clip: text;
    -webkit-background-clip: text;
}

selector .elementor-divider-separator:before{
     border: 1px solid;
     border-image:linear-gradient(to right, transparent, rgb(254, 255, 254)) 1;
}
selector .elementor-divider-separator:after{
     border: 1px solid;
     border-image:linear-gradient(to left, transparent, rgb(254, 255, 254)) 1;
}

selector .e-n-tabs-heading{
    display: flex;
    align-self: center;
    background: #0e0b149c;
    border: .1rem solid hsla(0, 0%, 100%, .12);
    border-radius: 20rem;
    cursor: pointer;
    padding: 5px 0px;
    position: sticky;
    top: 6rem;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
    width: 26.5rem;
    
}
selector:hover{
    background:none !important;
}
@media (max-width:767px){
selector .e-n-tabs-heading{
    width: 17rem;
}
}

/*arredondamento, altura e z-index*/
.card {
    border-radius: 5px;
    cursor: pointer;
    z-index: 2;
    min-height: 250px;
}
.card::before, .card::after {
    content: "";
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0px;
    left: 0px;
    border-radius: inherit;
    opacity: 0;
    transition: opacity 0.5s;
}

/*tamanho e fundo do conteudo*/
.card .card-content {
    background-color: #0f0d1c;
    border-radius: 5px;
    flex-direction: column;
    width: calc(100% - 2px);
    height: calc(100% - 2px);
    position: absolute;
    z-index: 3;
}

/*alinhamento*/
.card-content .elementor-widget-container{
    display: flex;
    align-items: center;
    justify-content: center;
}

/*degrade titulos*/
.card-content .elementor-image-box-title{
    background: linear-gradient(80deg, #2BA9F9 -10%, #2980B8 50%);
    background-clip: text;
    -webkit-background-clip: text;
    color: transparent !important;
}

/*degrade do card*/
.card::before {
    background: radial-gradient(800px circle at var(--mouse-x) var(--mouse-y), #352D7790, transparent 40%);
    mix-blend-mode: screen;
    z-index: 4;
}


/*degrade da borda*/
.card::after {  
    background: radial-gradient(400px circle at var(--mouse-x) var(--mouse-y), #2BA9F9, transparent 40%) !important;
}

/*opacidade do degradê no hover*/
.card:hover::before {
    opacity: 1;
}


/*opacidade do degradê da borda no hover*/
#cards:hover .card::after {
    opacity: 1;
}



@media(min-width: 371px) and (max-width: 400px){
    .card{
        min-height: 250px;
    }
}


@media(max-width: 1024px){
    .card-content{
        background: linear-gradient(40deg, #0f0d1c, #352D7770 );
        border-top: 1px solid;
        border-image: linear-gradient(80deg, transparent, #2BA9F9, transparent) 1;
    }
}


@media(max-width: 370px){
    .card{
        min-height: 270px;
    }
}
/*proíbe arrastar as imagens na página*/
img {
    user-select: none;
    -webkit-user-drag: none;
    -webkit-touch-callout: none;
}

/*configuração do texto aparecendo durante a rolagem da página*/
.scroll{
    opacity: 0;
    transition:1s;
    filter:blur(5px);
}
.ativo{
    opacity: 1;
    transition: 1s;
    filter:blur(0px);
}

/*configuração da barra de scroll*/
*::-webkit-scrollbar {
    width: 10px !important;
}
*::-webkit-scrollbar-track {
    background: #202020;
}
*::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, #2BA9F9, #2980B8);
    border-radius: 10px;
    border: 2px solid #202020;
}
 .backtop {
    color: #fff;
    background: rgb(79, 172, 254);
    background: linear-gradient(90deg, rgba(43, 165, 242, 1) 0%, rgba(41, 133, 193, 1) 100%);
    padding: 5px 10px;
    border-radius: 100px;
}

.iconn{
  position: absolute;
  top: 0;
  left: 0;
  width: 100%; 
  height: 100%;
  animation: bounce 2s infinite;
}
@keyframes bounce {
    0%, 100%, 20%, 50%, 80% {
        transform: translateY(0);
    }

    40% {
        transform: translateY(-10px);
    }

    60% {
        transform: translateY(-5px);
    }
}

@keyframes fadeIn {
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}
